/* Describes trees in terms of all members of a clade (i.e., no nodes listed). *//* Requires:   tree - a matrix containing members of each node cl (with nodes given as supraspecific numbers);		tree[cl][0] is the diversity of the node - this will change from branches to cumulative richness;		the last two lines give the branch lengths (in species) of each branch;			the first line (tree[notu-1]) gives it for species;			the second line (tree[notu]) gives it for internal nodes;		tree will be returned in modified form;	notu - number of taxa;	nodes - the number of clades;*******************************************************************************************************************************************/long **CladeMember(long **tree, int notu, int nodes){int	a, b, cl, f1, sp, dcl;for (cl=nodes-1; cl>=0; --cl)	{	for (f1=1; f1<=tree[cl][0]; ++f1)	{		sp=tree[cl][f1];		/* tally total diversity and cumulative membership */		if (sp>=notu)	{			dcl=sp-notu;			b=tree[dcl][0]-1;			for (a=tree[cl][0]; a>f1; --a)		tree[cl][a+b]=tree[cl][a];			for (a=f1; a<f1+tree[dcl][0]; ++a)	tree[cl][a]=tree[dcl][1+a-f1];			tree[cl][0]=tree[cl][0]+tree[dcl][0]-1;			f1=f1+tree[dcl][0]-1;			}		}	for (f1=tree[cl][0]+1; f1<notu; ++f1)	tree[cl][f1]=-1;	}return tree;}