/* Keep track of polymorphic characters */if (POLYMORPH>0)	{	polymat=imatrix(OTUs,POLYMORPH+1);	polysta=imatrix(OTUs,POLYCHAR+1);	polyOTU = ivector(OTUs);	for (sp=0; sp<OTUs; ++sp)	for (ch=0; ch<=POLYCHAR; ++ch)	{		polymat[sp][ch]=0;		polysta[sp][ch]=-5;		}	Matrix = fopen(CharData,"r");	for (sp=0; sp<OTUs; ++sp)	{	/*NOTE:  ZERO IS FOR THE OUTGROUP */		fscanf(Matrix,"%s",s);		polyOTU[sp]=j=0;		for (ch=0; ch<chars; ++ch)	{			if (s[ch]=='(')	{				++polyOTU[sp];				polymat[sp][j]=ch;				while (s[ch]!=')')	{					++ch;					polysta[sp][j]=s[ch]-'0';					if (polysta[sp][j]>9)						polysta[sp][j]=polysta[sp][j]-7;	/* Converts 'A' into 10, 'B' into 11, etc...*/					++j;					}				}			}			}	fclose(Matrix);	}