#include "analysis_clock.h"using namespace std;long ClockIn(){	long timer;		time(&timer);	// record analysis start time	return timer;}void ClockOut(long timer){ 	long secs, totaltime;	int hours, minutes, seconds; // Output Analysis Run Time	time(&secs);	totaltime=secs-timer;	if (totaltime>60)		{		minutes=totaltime/60;		seconds=totaltime%60;		if (minutes>60)			{			hours=minutes/60;			minutes=minutes%60;			cout << "\n\n Total Run-time:\t" << hours << ":";			if (minutes<10) cout << "0" << minutes << ":";			else  cout << minutes << ":";			if (seconds<10) cout << "0" << seconds << "\n";			else  cout << seconds << "\n";			}		else 			{			cout << "\n\n Total Run-time:\t" << minutes << ":";			if (seconds<10) cout << "0" << seconds << "\n";			else  cout << seconds << "\n";			}		}	else 		{		cout << "\n\n Total Run-time:\t" << totaltime << " seconds\n";		}}long GetClockTime(long timer){ 	long secs, totaltime; // Output Analysis Run Time	time(&secs);	totaltime=secs-timer;	return totaltime;}